// Classes (ES6)
class Animal {
  constructor(name, species) {
    this.name = name;
    this.species = species;
  }
  
  speak() {
    return `${this.name} makes a sound`;
  }
  
  getInfo() {
    return `${this.name} is a ${this.species}`;
  }
}

// Inheritance
class Dog extends Animal {
  constructor(name, breed) {
    super(name, 'dog'); // Call parent constructor
    this.breed = breed;
  }
  
  // Method overriding (polymorphism)
  speak() {
    return `${this.name} barks`;
  }
  
  // Additional method
  fetch() {
    return `${this.name} fetches the ball`;
  }
}

const dog = new Dog('Max', 'Labrador');
console.log(dog.speak()); // 'Max barks'
console.log(dog.getInfo()); // 'Max is a dog'

// Encapsulation - Private fields (ES2022)
class BankAccount {
  #balance = 0; // Private field
  
  constructor(initialBalance) {
    this.#balance = initialBalance;
  }
  
  deposit(amount) {
    if (amount > 0) {
      this.#balance += amount;
    }
  }
  
  getBalance() {
    return this.#balance;
  }
}

const account = new BankAccount(100);
// account.#balance; // Error: Private field

// Encapsulation - Closure pattern (pre-ES2022)
function createCounter() {
  let count = 0; // Private variable
  
  return {
    increment: () => ++count,
    decrement: () => --count,
    getCount: () => count
  };
}

// Polymorphism - Same interface, different behavior
class Shape {
  area() {
    throw new Error('Method must be implemented');
  }
}

class Circle extends Shape {
  constructor(radius) {
    super();
    this.radius = radius;
  }
  
  area() {
    return Math.PI * this.radius ** 2;
  }
}

class Rectangle extends Shape {
  constructor(width, height) {
    super();
    this.width = width;
    this.height = height;
  }
  
  area() {
    return this.width * this.height;
  }
}

const shapes = [
  new Circle(5),
  new Rectangle(4, 6)
];

shapes.forEach(shape => console.log(shape.area()));
// Polymorphism: same method call, different implementations

// Abstraction - Abstract classes (using inheritance)
class Vehicle {
  start() {
    throw new Error('Must implement start method');
  }
}

class Car extends Vehicle {
  start() {
    return 'Car engine started';
  }
}
