/* 1,0,0,0 - Inline style (highest) */
<div style="color: black;">Text</div>

/* !important overrides everything */
.text { color: red !important; }

/* Cascade order (when specificity is equal):
1. Source order (last one wins)
2. !important flag
3. Specificity
4. Inheritance
*/

/* Inheritance example */
.parent {
  color: blue;
  font-size: 16px;
}

.child {
  color: red; /* Overrides inherited color */
  /* font-size inherited from .parent */
}

/* Specificity examples */
