.box { background: red; }

/* Rule 2: 0,0,2,0 (wins) */
.container .box { background: blue; }

/* Rule 3: 0,1,0,0 (wins) */
#unique { background: green; }

/* Combining selectors increases specificity */
/* 0,0,3,1 */
div.container .box.item { }

/* Attribute selectors have same weight as classes */
/* 0,0,1,0 */
[type="text"] { }

/* Pseudo-classes have same weight as classes */
